mapper.select:
SELECT s.shopId, s.shopName, s.shopDesc, s.shopAddr, s.phone, s.shopImg, s.priority, s.createTime, s.lastEditTime, s.enableStatus, s.advice, a.areaId, a.areaName, sc.shopCategoryId, sc.shopCategoryName FROM tb_shop s, tb_area a, tb_shop_category sc
and s.shopCategoryId = #{shopCondition.shopCategory.shopCategoryId}
and s.shopCategoryId in (select shopCategoryId from tb_shop_category WHERE parentId = #{shopCondition.shopCategory.parent.shopCategoryId})
and s.areaId = #{shopCondition.area.areaId}
and s.shopName like '%${shopCondition.shopName}%'
and s.enableStatus = #{shopCondition.enableStatus}
and s.ownerId = #{shopCondition.owner.userId}
AND s.areaId=a.areaId AND s.shopCategoryId = sc.shopCategoryId
ORDER BY s.priority DESC LIMIT #{rowIndex},#{pageSize};

SELECT count(1) FROM tb_shop s, tb_area a, tb_shop_category sc
and s.shopCategoryId = #{shopCondition.shopCategory.shopCategoryId}
and s.shopCategoryId in (select shopCategoryId from tb_shop_category WHERE parentId = #{shopCondition.shopCategory.parent.shopCategoryId})
and s.areaId = #{shopCondition.area.areaId}
and s.shopName like '%${shopCondition.shopName}%'
and s.enableStatus = #{shopCondition.enableStatus}
and s.ownerId = #{shopCondition.owner.userId}
AND s.areaId=a.areaId AND s.shopCategoryId = sc.shopCategoryId

mapper.update:
update tb_shop
shopName=#{shopName}, shopDesc=#{shopDesc}, shopAddr=#{shopAddr}, phone=#{phone}, shopImg=#{shopImg}, priority=#{priority}, lastEditTime=#{lastEditTime}, enableStatus=#{enableStatus}, advice=#{advice}, areaId=#{area.areaId}, shopCategoryId=#{shopCategory.shopCategoryId}
where shopId=#{shopId}
